<script type="text/javascript" charset="utf-8">
	"use strict";
	(() => {
		const d = document.getElementsByClassName("d"),
			info = document.getElementById("d_sign-info");
		function reset() {
			Array.from(d).forEach(e => {
				e.style.fill = "none";
				e.style.stroke = "none";
			});
			(() => {
				const orgn = [
					["box1", 155, 186],
					["box2", 50, 195],
					["box3", 400, 230],
					["s_key", 144.4, 32.4],
					["p_key", 198, 32.4],
					["ico_hash", 232.5, 62.5],
					["ico_hash0", 232.5, 62.5],
					["doc", 50, 102]
				];
				orgn.forEach(e => {
					const n = e[0],
						x = e[1],
						y = e[2],
						obj = document.getElementById(n),
						t = obj.tagName.toLowerCase();
					if (t === "path") {
						let d = obj.getAttribute("d");
						d = d.replace(/\d+\.*\d*(?=,)/, x);
						d = d.replace(/,\d+\.*\d*/, "," + y);
						obj.setAttribute("d", d);
					} else if (t === "rect") {
						obj.setAttribute("x", x);
						obj.setAttribute("y", y);
					}
				});
				info.textContent = "";
			})();
		}
		reset();
		let x_lock = false,
			now = 0;
		document.getElementById("d_sign-button").addEventListener("click", () => {
			if (!x_lock) {
				x_lock = true;
				switch (now) {
					case 0:
						document.getElementById("arw0").style.fill = "#00FF00";
